import React, { useState } from "react";
import { uploadAndAnalyzeVideo } from "../services/api_client";

const Dashboard = () => {
    const [file, setFile] = useState(null);
    const [loading, setLoading] = useState(false);
    const [result, setResult] = useState(null);

    const handleFileChange = (e) => {
        setFile(e.target.files[0]);
        setResult(null); 
    };

    const handleUpload = async () => {
        if (!file) {
            alert("Bhai, pehle koi video select toh karo!");
            return;
        }
        setLoading(true);
        const apiResult = await uploadAndAnalyzeVideo(file);
        setResult(apiResult);
        setLoading(false);
    };

    return (
        <div style={{ padding: "40px", animation: "fadeIn 0.8s ease-in" }}>
            <div style={{ 
                maxWidth: "850px", margin: "0 auto", backgroundColor: "#1e293b", 
                padding: "40px", borderRadius: "20px", boxShadow: "0 20px 50px rgba(0,0,0,0.5)",
                border: "1px solid #334155" 
            }}>
                <h2 style={{ textAlign: "center", color: "#38bdf8", letterSpacing: "1px", marginBottom: "30px" }}>
                    VIDEO FORENSICS ANALYZER
                </h2>
                
                {/* Custom Animated Upload Box */}
                <div style={{ 
                    border: "2px dashed #475569", padding: "40px", textAlign: "center", 
                    borderRadius: "15px", backgroundColor: "#0f172a", marginBottom: "30px",
                    transition: "all 0.3s ease"
                }}>
                    <input type="file" onChange={handleFileChange} style={{ color: "#94a3b8", cursor: "pointer" }} />
                    <p style={{ marginTop: "10px", color: "#64748b", fontSize: "0.9rem" }}>Supported formats: MP4, AVI, MOV (Max 50MB)</p>
                </div>

                {/* The "Cool" Animated Button */}
                <button 
                    onClick={handleUpload} 
                    disabled={loading}
                    className={loading ? "pulse-animation" : ""}
                    style={{ 
                        width: "100%", padding: "18px", fontSize: "18px", fontWeight: "bold",
                        borderRadius: "12px", border: "none", cursor: loading ? "not-allowed" : "pointer",
                        backgroundColor: loading ? "#1e293b" : "#3b82f6",
                        color: "white", transition: "all 0.4s ease",
                        boxShadow: loading ? "none" : "0 8px 15px rgba(59, 130, 246, 0.3)",
                        display: "flex", justifyContent: "center", alignItems: "center", gap: "10px"
                    }}
                >
                    {loading ? (
                        <>
                            <span className="spinner"></span> ðŸ§¬ ANALYZING BIOMETRICS...
                        </>
                    ) : (
                        "ðŸš€ START DEEP ANALYSIS"
                    )}
                </button>

                {/* Result Section with Animation */}
                {result && result.status === "success" && (
                    <div style={{ marginTop: "40px", animation: "slideUp 0.6s ease-out" }}>
                        <div style={{ 
                            padding: "30px", borderRadius: "15px", textAlign: "center",
                            background: result.verdict === "FAKE" 
                                ? "linear-gradient(145deg, #7f1d1d, #450a0a)" 
                                : "linear-gradient(145deg, #14532d, #06200c)",
                            border: `2px solid ${result.verdict === "FAKE" ? "#ef4444" : "#22c55e"}`
                        }}>
                            <h1 style={{ margin: "0", fontSize: "3rem", color: "white" }}>{result.verdict}</h1>
                            <p style={{ fontSize: "1.2rem", color: "#cbd5e1" }}>Threat Level: <strong>{result.confidence}%</strong></p>
                        </div>
                    </div>
                )}
            </div>

            {/* In-line CSS for Animations */}
            <style>{`
                @keyframes fadeIn { from { opacity: 0; } to { opacity: 1; } }
                @keyframes slideUp { from { transform: translateY(20px); opacity: 0; } to { transform: translateY(0); opacity: 1; } }
                @keyframes spin { 0% { transform: rotate(0deg); } 100% { transform: rotate(360deg); } }
                @keyframes pulse { 0% { box-shadow: 0 0 0 0 rgba(59, 130, 246, 0.7); } 70% { box-shadow: 0 0 0 15px rgba(59, 130, 246, 0); } 100% { box-shadow: 0 0 0 0 rgba(59, 130, 246, 0); } }
                
                .spinner {
                    width: 20px; height: 20px; border: 3px solid #f3f3f3;
                    border-top: 3px solid #38bdf8; border-radius: 50%;
                    animation: spin 1s linear infinite;
                }
                .pulse-animation { animation: pulse 1.5s infinite; }
            `}</style>
        </div>
    );
};

export default Dashboard;
