import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import axios from 'axios';

const Dashboard = () => {
    const [file, setFile] = useState(null);
    const [loading, setLoading] = useState(false);
    const [result, setResult] = useState(null);
    const [userName, setUserName] = useState("Investigator");
    const navigate = useNavigate();

    useEffect(() => {
        // Login ke waqt jo name save kiya tha wo uthao
        const savedName = localStorage.getItem('userName');
        if (savedName) setUserName(savedName);
    }, []);

    const handleFileChange = (e) => {
        setFile(e.target.files[0]);
        setResult(null); 
    };

    const handleUpload = async () => {
        if (!file) {
            alert("Bhai, pehle koi video select toh karo!");
            return;
        }

        const userID = localStorage.getItem('user_id') || 1;
        const formData = new FormData();
        formData.append('file', file);
        formData.append('user_id', userID);

        setLoading(true);
        try {
            const response = await axios.post('http://127.0.0.1:8000/api/analyze', formData);
            setResult(response.data);
        } catch (error) {
            alert("Analysis failed. Check backend connection.");
        } finally {
            setLoading(false);
        }
    };

    const handleLogout = () => {
        localStorage.clear();
        navigate('/');
    };

    return (
        <div style={styles.appContainer}>
            {/* --- 1. SIDEBAR NAVIGATION --- */}
            <div style={styles.sidebar}>
                <h3 style={{color: '#38bdf8', textAlign: 'center'}}>üõ°Ô∏è DEEPGUARD</h3>
                <div style={styles.navLinks}>
                    <div style={styles.activeNavLink}>üìä Dashboard</div>
                    <div style={styles.navLink} onClick={() => navigate('/history')}>üìú History Log</div>
                    <div style={styles.navLink} onClick={() => navigate('/admin-panel')}>üîë Admin Access</div>
                </div>
                <button onClick={handleLogout} style={styles.logoutBtn}>Logout System</button>
            </div>

            {/* --- 2. MAIN CONTENT --- */}
            <div style={styles.mainContent}>
                <header style={styles.header}>
                    <div>
                        <h2 style={{margin: 0}}>Forensic Workstation</h2>
                        <p style={{color: '#94a3b8', fontSize: '14px'}}>Welcome back, {userName}</p>
                    </div>
                    <div style={styles.statusBadge}>System: Operational</div>
                </header>

                <div style={styles.uploadCard}>
                    <h3 style={{color: '#38bdf8', marginBottom: '20px'}}>Initiate Video Scan</h3>
                    <div style={styles.dropZone}>
                        <input type="file" onChange={handleFileChange} style={{color: '#94a3b8'}} />
                        <p style={{fontSize: '12px', color: '#64748b', marginTop: '10px'}}>Drag & drop or click to select evidence</p>
                    </div>

                    <button 
                        onClick={handleUpload} 
                        disabled={loading}
                        style={styles.scanBtn(loading)}
                    >
                        {loading ? "üß¨ EXTRACTING BIOMETRICS..." : "üöÄ START DEEP ANALYSIS"}
                    </button>
                </div>

                {/* --- 3. DETAILED RESULTS SECTION --- */}
                {result && (
                    <div style={{marginTop: '30px', animation: 'slideUp 0.5s ease'}}>
                        <div style={styles.resultHeader(result.verdict)}>
                            <h1 style={{margin: 0, fontSize: '3rem'}}>{result.verdict}</h1>
                            <p>Confidence Level: <strong>{result.confidence}%</strong></p>
                        </div>

                        <div style={styles.metricsGrid}>
                            <MetricBox label="Spatial Analysis" score={result.branch_scores?.spatial || 0} />
                            <MetricBox label="Physics Consistency" score={result.branch_scores?.physics || 0} />
                            <MetricBox label="Forensic Traces" score={result.branch_scores?.forensics || 0} />
                            <MetricBox label="Audio Biometrics" score={result.branch_scores?.audio || 0} />
                        </div>
                    </div>
                )}
            </div>
        </div>
    );
};

// Sub-component for individual Metric Bars
const MetricBox = ({ label, score }) => (
    <div style={styles.metricBox}>
        <div style={{display: 'flex', justifyContent: 'space-between', marginBottom: '5px'}}>
            <span style={{fontSize: '13px', color: '#94a3b8'}}>{label}</span>
            <span style={{fontSize: '13px', color: '#f8fafc'}}>{score}%</span>
        </div>
        <div style={styles.barBg}>
            <div style={styles.barFill(score)}></div>
        </div>
    </div>
);

const styles = {
    appContainer: { display: 'flex', height: '100vh', backgroundColor: '#020617', color: 'white', fontFamily: 'Inter, sans-serif' },
    sidebar: { width: '260px', backgroundColor: '#0f172a', padding: '30px', borderRight: '1px solid #1e293b', display: 'flex', flexDirection: 'column' },
    navLinks: { marginTop: '50px', flex: 1 },
    navLink: { padding: '15px', borderRadius: '10px', cursor: 'pointer', color: '#94a3b8', transition: '0.3s', marginBottom: '5px' },
    activeNavLink: { padding: '15px', borderRadius: '10px', backgroundColor: '#1e293b', color: '#38bdf8', fontWeight: 'bold' },
    logoutBtn: { padding: '12px', backgroundColor: '#ef444422', color: '#ef4444', border: '1px solid #ef4444', borderRadius: '8px', cursor: 'pointer' },
    
    mainContent: { flex: 1, padding: '40px', overflowY: 'auto' },
    header: { display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '40px' },
    statusBadge: { padding: '5px 15px', backgroundColor: '#064e3b', color: '#10b981', borderRadius: '20px', fontSize: '12px', border: '1px solid #10b981' },
    
    uploadCard: { backgroundColor: '#1e293b', padding: '30px', borderRadius: '20px', border: '1px solid #334155' },
    dropZone: { border: '2px dashed #475569', padding: '30px', textAlign: 'center', borderRadius: '15px', backgroundColor: '#0f172a', marginBottom: '20px' },
    
    scanBtn: (loading) => ({
        width: '100%', padding: '15px', borderRadius: '10px', border: 'none', fontWeight: 'bold',
        backgroundColor: loading ? '#334155' : '#38bdf8', color: '#0f172a', cursor: loading ? 'not-allowed' : 'pointer'
    }),

    resultHeader: (verdict) => ({
        padding: '30px', borderRadius: '20px', textAlign: 'center',
        background: verdict === "FAKE" ? 'linear-gradient(135deg, #991b1b, #450a0a)' : 'linear-gradient(135deg, #065f46, #064e3b)',
        border: `2px solid ${verdict === "FAKE" ? "#ef4444" : "#10b981"}`
    }),
    
    metricsGrid: { display: 'grid', gridTemplateColumns: '1fr 1fr', gap: '20px', marginTop: '20px', padding: '20px', backgroundColor: '#0f172a', borderRadius: '15px' },
    metricBox: { padding: '10px' },
    barBg: { height: '6px', backgroundColor: '#1e293b', borderRadius: '3px' },
    barFill: (score) => ({ height: '100%', width: `${score}%`, backgroundColor: score > 70 ? '#ef4444' : '#38bdf8', borderRadius: '3px', transition: 'width 1s ease-in-out' })
};

export default Dashboard;
