import React, { useState, useEffect } from "react";
import axios from 'axios';
import Sidebar from "../components/Sidebar";
import Navbar from "../components/Navbar";

const Dashboard = () => {
    const [file, setFile] = useState(null);
    const [previewUrl, setPreviewUrl] = useState(null); // Video preview state
    const [loading, setLoading] = useState(false);
    const [result, setResult] = useState(null);
    const [userName, setUserName] = useState("Investigator");

    useEffect(() => {
        const savedName = localStorage.getItem('userName');
        if (savedName) setUserName(savedName);
    }, []);

    const handleFileChange = (e) => {
        const selectedFile = e.target.files[0];
        if (selectedFile) {
            setFile(selectedFile);
            setPreviewUrl(URL.createObjectURL(selectedFile)); // Create local URL for preview
            setResult(null); 
        }
    };

    const handleUpload = async () => {
        if (!file) return alert("‚ö†Ô∏è Select evidence first!");
        const userID = localStorage.getItem('user_id') || 1;
        const formData = new FormData();
        formData.append('file', file);
        formData.append('user_id', userID);

        setLoading(true);
        try {
            const response = await axios.post('http://127.0.0.1:8000/api/analyze', formData);
            setResult(response.data);
        } catch (error) {
            alert("‚ùå System Error: Forensic Engine Offline.");
        } finally {
            setLoading(false);
        }
    };

    return (
        <div style={styles.pageWrapper}>
            <Sidebar />

            <div style={styles.mainArea}>
                <Navbar />

                <div style={styles.contentPadding}>
                    <header style={styles.welcomeHeader}>
                        <div>
                            <h1 style={styles.mainTitle}>Forensic Workstation</h1>
                            <p style={styles.subTitle}>Subject: <span style={{color: '#38bdf8'}}>{userName}</span></p>
                        </div>
                        <div style={styles.liveBadge}>‚óè ENGINE ONLINE</div>
                    </header>

                    {/* --- Upload Terminal --- */}
                    <div style={styles.uploadTerminal}>
                        {!previewUrl ? (
                            <div style={styles.dropZone}>
                                <div style={{fontSize: '50px'}}>üì°</div>
                                <h3 style={{color: '#f8fafc'}}>Inject Evidence Data</h3>
                                <label style={styles.fileButton}>
                                    SELECT SOURCE
                                    <input type="file" onChange={handleFileChange} style={{ display: 'none' }} />
                                </label>
                            </div>
                        ) : (
                            <div style={styles.previewContainer}>
                                <div style={styles.videoHeader}>EVIDENCE_PLAYBACK_V.01</div>
                                <video src={previewUrl} controls style={styles.videoElement} />
                                <button onClick={() => {setFile(null); setPreviewUrl(null);}} style={styles.changeBtn}>CHANGE SOURCE</button>
                            </div>
                        )}

                        <button 
                            onClick={handleUpload} 
                            disabled={loading || !file}
                            style={styles.scanBtn(loading || !file)}
                            className={loading ? "pulse-glow" : ""}
                        >
                            {loading ? "EXTRACTING BIOMETRICS..." : "INITIATE DEEP SCAN"}
                        </button>
                    </div>

                    {/* --- Analysis Section (Grid Layout) --- */}
                    {result && (
                        <div style={{ marginTop: '40px', animation: 'slideUp 0.6s ease' }}>
                            <div style={styles.verdictPanel(result.verdict)}>
                                <h1 style={styles.verdictText}>{result.verdict}</h1>
                                <p>CONFIDENCE: <b>{result.confidence}%</b></p>
                            </div>

                            <div style={styles.resultsGrid}>
                                {/* Live Analysis View (Cool Video View with Scanner) */}
                                <div style={styles.liveViewCard}>
                                    <div style={styles.scannerLine}></div>
                                    <video src={previewUrl} autoPlay loop muted style={styles.miniVideo} />
                                    <div style={styles.overlayText}>SCANNING_SIGNAL_HASH...</div>
                                </div>

                                {/* Technical Metrics */}
                                <div style={styles.metricsContainer}>
                                    <h4 style={styles.metricsTitle}>üî¨ SIGNAL ANALYSIS</h4>
                                    <MetricBar label="Spatial Consistency" score={result.branch_scores?.spatial || 0} />
                                    <MetricBar label="Physical Integrity" score={result.branch_scores?.physics || 0} />
                                    <MetricBar label="Digital Forensics" score={result.branch_scores?.forensics || 0} />
                                    <MetricBar label="Audio Biometrics" score={result.branch_scores?.audio || 0} />
                                </div>
                            </div>
                        </div>
                    )}
                </div>
            </div>
            
            <style>{`
                @keyframes slideUp { from { transform: translateY(40px); opacity: 0; } to { transform: translateY(0); opacity: 1; } }
                @keyframes scan { 0% { top: 0; } 100% { top: 100%; } }
                .pulse-glow { animation: glowAnim 1.5s infinite; }
                @keyframes glowAnim { 0% { box-shadow: 0 0 0 0 rgba(56, 189, 248, 0.6); } 70% { box-shadow: 0 0 0 20px rgba(56, 189, 248, 0); } 100% { box-shadow: 0 0 0 0 rgba(56, 189, 248, 0); } }
            `}</style>
        </div>
    );
};

const MetricBar = ({ label, score }) => (
    <div style={{ marginBottom: '18px' }}>
        <div style={{ display: 'flex', justifyContent: 'space-between', marginBottom: '5px' }}>
            <span style={{ fontSize: '12px', color: '#94a3b8' }}>{label}</span>
            <span style={{ fontSize: '12px', color: '#f8fafc', fontWeight: 'bold' }}>{score}%</span>
        </div>
        <div style={{ height: '6px', backgroundColor: '#0f172a', borderRadius: '3px', border: '1px solid #1e293b' }}>
            <div style={{ 
                height: '100%', width: `${score}%`, 
                backgroundColor: score > 75 ? '#ef4444' : '#38bdf8', 
                borderRadius: '3px', transition: 'width 1.5s ease-out'
            }}></div>
        </div>
    </div>
);

