import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

const AdminLogin = () => {
    const navigate = useNavigate();
    const [password, setPassword] = useState('');

    const handleLogin = (e) => {
        e.preventDefault();
        if (password === 'admin123') { // Secret Password
            localStorage.setItem('userRole', 'admin');
            navigate('/dashboard');
        } else {
            alert("‚ö†Ô∏è ACCESS DENIED: Invalid Security Key");
        }
    };

    return (
        <div style={styles.fullPage}>
            <div style={styles.loginCard}>
                <h2 style={{color: '#ef4444'}}>üõ°Ô∏è Admin Command Center</h2>
                <p style={{color: '#94a3b8'}}>High-level authorization required</p>
                <form onSubmit={handleLogin} style={{marginTop: '20px'}}>
                    <input 
                        type="password" 
                        placeholder="Security Key" 
                        style={styles.input}
                        onChange={(e) => setPassword(e.target.value)}
                        required
                    />
                    <button type="submit" style={styles.adminBtn}>Authorize Access</button>
                </form>
                <p onClick={() => navigate('/')} style={styles.backLink}>‚Üê Return to Gateway</p>
            </div>
        </div>
    );
};

const styles = {
    fullPage: { height: '100vh', display: 'flex', justifyContent: 'center', alignItems: 'center', backgroundColor: '#020617' },
    loginCard: { backgroundColor: '#1e293b', padding: '40px', borderRadius: '15px', textAlign: 'center', width: '350px', border: '1px solid #ef4444', boxShadow: '0 0 20px rgba(239, 68, 68, 0.2)' },
    input: { width: '100%', padding: '12px', marginBottom: '20px', borderRadius: '8px', border: '1px solid #334155', backgroundColor: '#0f172a', color: 'white' },
    adminBtn: { width: '100%', padding: '12px', backgroundColor: '#ef4444', color: 'white', border: 'none', borderRadius: '8px', cursor: 'pointer', fontWeight: 'bold' },
    backLink: { color: '#64748b', marginTop: '20px', cursor: 'pointer', fontSize: '14px' }
};

export default AdminLogin;
