import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

const UserLogin = () => {
    const navigate = useNavigate();
    const [name, setName] = useState('');

    const handleLogin = (e) => {
        e.preventDefault();
        if (name.trim()) {
            localStorage.setItem('userRole', 'user');
            localStorage.setItem('userName', name);
            navigate('/dashboard');
        }
    };

    return (
        <div style={styles.fullPage}>
            <div style={styles.loginCard}>
                <h2 style={{color: '#38bdf8'}}>üë§ User Access Portal</h2>
                <p style={{color: '#94a3b8'}}>Please enter your identity to continue</p>
                <form onSubmit={handleLogin} style={{marginTop: '20px'}}>
                    <input 
                        type="text" 
                        placeholder="Operator Name / ID" 
                        style={styles.input}
                        onChange={(e) => setName(e.target.value)}
                        required
                    />
                    <button type="submit" style={styles.userBtn}>Initialize Session</button>
                </form>
                <p onClick={() => navigate('/')} style={styles.backLink}>‚Üê Back to Gateway</p>
            </div>
        </div>
    );
};

const styles = {
    fullPage: { height: '100vh', display: 'flex', justifyContent: 'center', alignItems: 'center', backgroundColor: '#020617' },
    loginCard: { backgroundColor: '#1e293b', padding: '40px', borderRadius: '15px', textAlign: 'center', width: '350px', border: '1px solid #38bdf8' },
    input: { width: '100%', padding: '12px', marginBottom: '20px', borderRadius: '8px', border: '1px solid #334155', backgroundColor: '#0f172a', color: 'white' },
    userBtn: { width: '100%', padding: '12px', backgroundColor: '#3b82f6', color: 'white', border: 'none', borderRadius: '8px', cursor: 'pointer', fontWeight: 'bold' },
    backLink: { color: '#64748b', marginTop: '20px', cursor: 'pointer', fontSize: '14px' }
};

export default UserLogin;
