import React from 'react';
import { Link, useLocation, useNavigate } from 'react-router-dom';

const Sidebar = () => {
    const location = useLocation();
    const navigate = useNavigate();
    const userRole = localStorage.getItem('role');

    return (
        <div style={styles.sidebar}>
            <div style={styles.logoSection}>
                <div style={styles.logoIcon}>üèõÔ∏è</div>
                <h3 style={styles.logoText}>DeepGuard</h3>
                <span style={styles.adminTag}>{userRole?.toUpperCase()} MODE</span>
            </div>

            <div style={styles.section}>
                <p style={styles.label}>CORE WORKSPACE</p>
                <Link to="/dashboard" style={styles.navLink(location.pathname === '/dashboard')}>
                    üìä Dashboard
                </Link>
                <Link to="/history" style={styles.navLink(location.pathname === '/history')}>
                    üìú Forensic History
                </Link>
            </div>

            {userRole === 'admin' && (
                <div style={styles.section}>
                    <p style={styles.label}>IDENTITY MANAGEMENT</p>
                    <Link to="/assign-member" style={styles.navLink(location.pathname === '/assign-member')}>
                        ‚ûï Assign New Member
                    </Link>
                    <Link to="/user-management" style={styles.navLink(location.pathname === '/user-management')}>
                        üë• User Management
                    </Link>
                </div>
            )}

            <div style={styles.footer}>
                <button onClick={() => { localStorage.clear(); navigate('/'); }} style={styles.logoutBtn}>
                    EXIT SYSTEM
                </button>
            </div>
        </div>
    );
};

const styles = {
    sidebar: { width: '260px', backgroundColor: '#0f172a', height: '100vh', padding: '30px 20px', borderRight: '1px solid #1e293b', display: 'flex', flexDirection: 'column', position: 'fixed' },
    logoSection: { textAlign: 'center', marginBottom: '40px' },
    logoIcon: { fontSize: '45px', marginBottom: '10px' },
    logoText: { margin: 0, color: '#38bdf8', letterSpacing: '1px' },
    adminTag: { fontSize: '10px', color: '#64748b', fontWeight: 'bold', letterSpacing: '2px' },
    section: { marginBottom: '30px' },
    label: { fontSize: '11px', color: '#475569', letterSpacing: '1.5px', marginBottom: '15px', paddingLeft: '12px' },
    navLink: (active) => ({
        display: 'block', padding: '12px 15px', borderRadius: '12px', textDecoration: 'none',
        color: active ? '#38bdf8' : '#94a3b8', backgroundColor: active ? '#1e293b' : 'transparent',
        marginBottom: '8px', fontWeight: active ? 'bold' : 'normal', transition: '0.3s'
    }),
    footer: { marginTop: 'auto' },
    logoutBtn: { width: '100%', padding: '12px', border: '1px solid #ef4444', backgroundColor: 'transparent', color: '#ef4444', borderRadius: '10px', cursor: 'pointer', fontWeight: 'bold', fontSize: '12px', letterSpacing: '1px' }
};

export default Sidebar;
