import React, { useState, useEffect } from "react";
import axios from 'axios';
import Sidebar from "../components/Sidebar";
import Navbar from "../components/Navbar";

const Dashboard = () => {
    const [file, setFile] = useState(null);
    const [loading, setLoading] = useState(false);
    const [result, setResult] = useState(null);
    const [userName, setUserName] = useState("Investigator");

    useEffect(() => {
        const savedName = localStorage.getItem('userName');
        if (savedName) setUserName(savedName);
    }, []);

    const handleFileChange = (e) => {
        if (e.target.files[0]) {
            setFile(e.target.files[0]);
            setResult(null); 
        }
    };

    const handleUpload = async () => {
        if (!file) {
            alert("‚ö†Ô∏è Please select an evidence file first!");
            return;
        }

        const userID = localStorage.getItem('user_id') || 1;
        const formData = new FormData();
        formData.append('file', file);
        formData.append('user_id', userID);

        setLoading(true);
        try {
            // Check if backend is running on 8000
            const response = await axios.post('http://127.0.0.1:8000/api/analyze', formData);
            setResult(response.data);
        } catch (error) {
            console.error(error);
            alert("‚ùå System Error: Governance Engine (Backend) is offline or unreachable.");
        } finally {
            setLoading(false);
        }
    };

    return (
        <div style={styles.pageWrapper}>
            <Sidebar />

            <div style={styles.mainArea}>
                <Navbar />

                <div style={styles.contentPadding}>
                    <header style={styles.welcomeHeader}>
                        <div style={styles.titleGroup}>
                            <h2 style={styles.mainTitle}>Forensic Workstation</h2>
                            <p style={styles.subTitle}>Logged in as: <span style={{color: '#38bdf8'}}>{userName}</span></p>
                        </div>
                        <div style={styles.pulseBadge}>
                            <span style={styles.pulseDot}></span> System Live: AI Engine Active
                        </div>
                    </header>

                    {/* --- Stylish Upload Section --- */}
                    <div style={styles.uploadSection}>
                        <div style={styles.dropZone}>
                            <div style={styles.uploadIcon}>{file ? "‚úÖ" : "üïµÔ∏è‚Äç‚ôÇÔ∏è"}</div>
                            <h3 style={{color: '#f8fafc', marginBottom: '5px'}}>
                                {file ? file.name : "Select Evidence Video"}
                            </h3>
                            <p style={{ color: '#64748b', fontSize: '13px', marginBottom: '20px' }}>
                                Drag & Drop or click to browse (MP4, AVI, MOV)
                            </p>
                            
                            {/* Hidden Default Input, Styled Label */}
                            <label style={styles.fileLabel}>
                                BROWSE FILE
                                <input type="file" onChange={handleFileChange} style={{ display: 'none' }} />
                            </label>
                        </div>

                        <button 
                            onClick={handleUpload} 
                            disabled={loading || !file}
                            style={styles.scanBtn(loading || !file)}
                            className={loading ? "pulse-btn" : ""}
                        >
                            {loading ? "üß¨ EXTRACTING BIOMETRICS..." : "üöÄ INITIATE FORENSIC SCAN"}
                        </button>
                    </div>

                    {/* --- Detailed Results Area --- */}
                    {result && (
                        <div style={{ marginTop: '40px', animation: 'slideUp 0.6s ease' }}>
                            <div style={styles.verdictBanner(result.verdict)}>
                                <span style={styles.verdictLabel}>GOVERNANCE VERDICT</span>
                                <h1 style={styles.verdictText}>{result.verdict}</h1>
                                <div style={styles.verdictStats}>
                                    <span>Confidence: <b>{result.confidence}%</b></span>
                                    <span style={styles.statDivider}>|</span>
                                    <span>Time: <b>{result.processing_time}</b></span>
                                </div>
                            </div>

                            <div style={styles.metricsGrid}>
                                <h3 style={styles.gridTitle}>üî¨ TECHNICAL METRICS</h3>
                                <div style={styles.gridContent}>
                                    <MetricBar label="Spatial Consistency" score={result.branch_scores?.spatial || 0} />
                                    <MetricBar label="Physical Integrity" score={result.branch_scores?.physics || 0} />
                                    <MetricBar label="Digital Forensics" score={result.branch_scores?.forensics || 0} />
                                    <MetricBar label="Audio Biometrics" score={result.branch_scores?.audio || 0} />
                                </div>
                            </div>
                        </div>
                    )}
                </div>
            </div>
            
            <style>{`
                @keyframes slideUp { from { transform: translateY(40px); opacity: 0; } to { transform: translateY(0); opacity: 1; } }
                .pulse-btn { animation: pulseAnim 1.5s infinite; }
                @keyframes pulseAnim { 0% { opacity: 1; box-shadow: 0 0 0 0 rgba(56, 189, 248, 0.7); } 70% { box-shadow: 0 0 0 20px rgba(56, 189, 248, 0); } 100% { opacity: 1; } }
            `}</style>
        </div>
    );
};

// Internal Progress Bar Component
const MetricBar = ({ label, score }) => (
    <div style={{ marginBottom: '20px' }}>
        <div style={{ display: 'flex', justifyContent: 'space-between', marginBottom: '8px' }}>
            <span style={{ fontSize: '13px', color: '#94a3b8', letterSpacing: '0.5px' }}>{label}</span>
            <span style={{ fontSize: '13px', color: '#f8fafc', fontWeight: 'bold' }}>{score}%</span>
        </div>
        <div style={{ height: '8px', backgroundColor: '#0f172a', borderRadius: '4px', border: '1px solid #1e293b' }}>
            <div style={{ 
