// frontend/src/pages/Dashboard.jsx

import React, { useState } from "react";
import { uploadAndAnalyzeVideo } from "../services/api_client";

const Dashboard = () => {
    const [file, setFile] = useState(null);
    const [loading, setLoading] = useState(false);
    const [result, setResult] = useState(null);

    const handleFileChange = (e) => {
        setFile(e.target.files[0]);
        setResult(null); 
    };

    const handleUpload = async () => {
        if (!file) {
            alert("Bhai, pehle koi video select toh karo!");
            return;
        }

        setLoading(true);
        const apiResult = await uploadAndAnalyzeVideo(file);
        setResult(apiResult);
        setLoading(false);
    };

    return (
        <div style={{ padding: "40px", color: "#e2e8f0" }}>
            <div style={{ maxWidth: "800px", margin: "0 auto", backgroundColor: "#1e293b", padding: "30px", borderRadius: "15px", boxShadow: "0 4px 20px rgba(0,0,0,0.3)" }}>
                <h2 style={{ textAlign: "center", color: "#38bdf8" }}>Deepfake Analysis Center</h2>
                
                {/* Upload Box */}
                <div style={{ border: "2px dashed #475569", padding: "30px", textAlign: "center", borderRadius: "10px", margin: "20px 0" }}>
                    <input type="file" onChange={handleFileChange} style={{ color: "#fff" }} />
                    <button 
                        onClick={handleUpload} 
                        disabled={loading}
                        style={{ marginTop: "15px", padding: "10px 20px", backgroundColor: "#3b82f6", color: "white", border: "none", borderRadius: "5px", cursor: "pointer", width: "100%" }}
                    >
                        {loading ? "AI is Scanning... ðŸ§¬" : "Scan Video Now"}
                    </button>
                </div>

                {/* Result Display */}
                {result && result.status === "success" && (
                    <div style={{ marginTop: "20px", padding: "20px", backgroundColor: "#0f172a", borderRadius: "10px" }}>
                        <h3 style={{ color: result.verdict === "FAKE" ? "#ef4444" : "#22c55e", textAlign: "center", fontSize: "2rem" }}>
                            {result.verdict} DETECTED
                        </h3>
                        <p style={{ textAlign: "center" }}>Confidence Score: <b>{result.confidence}%</b></p>
                        
                        <div style={{ marginTop: "20px" }}>
                            {Object.entries(result.branch_scores).map(([name, score]) => (
                                <div key={name} style={{ marginBottom: "10px" }}>
                                    <small style={{ textTransform: "capitalize" }}>{name} Analysis: {score}%</small>
                                    <div style={{ height: "6px", backgroundColor: "#334155", borderRadius: "3px" }}>
                                        <div style={{ width: `${score}%`, height: "100%", backgroundColor: "#38bdf8", borderRadius: "3px" }}></div>
                                    </div>
                                </div>
                            ))}
                        </div>
                    </div>
                )}
            </div>
        </div>
    );
};

export default Dashboard;
