import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

const UserLogin = () => {
    const navigate = useNavigate();
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');

    const handleLogin = (e) => {
        e.preventDefault();
        // User login logic: Any email and password longer than 4 chars
        if (email.includes('@') && password.length >= 5) {
            localStorage.setItem('userRole', 'user');
            localStorage.setItem('userEmail', email);
            navigate('/dashboard');
        } else {
            alert("Please enter a valid email and password (min 5 characters).");
        }
    };

    return (
        <div style={styles.fullPage}>
            <div style={styles.loginCard}>
                <div style={styles.iconCircle}>üë§</div>
                <h2 style={{ color: '#38bdf8', marginBottom: '10px' }}>User Access Portal</h2>
                <p style={{ color: '#94a3b8', fontSize: '14px', marginBottom: '25px' }}>Enter your credentials to analyze video</p>
                
                <form onSubmit={handleLogin}>
                    <input 
                        type="email" 
                        placeholder="Email Address" 
                        style={styles.input}
                        onChange={(e) => setEmail(e.target.value)}
                        required
                    />
                    <input 
                        type="password" 
                        placeholder="Password" 
                        style={styles.input}
                        onChange={(e) => setPassword(e.target.value)}
                        required
                    />
                    <button type="submit" style={styles.userBtn}>Initialize Session</button>
                </form>
                
                <p onClick={() => navigate('/')} style={styles.backLink}>‚Üê Back to Gateway</p>
            </div>
        </div>
    );
};

// --- STYLES OBJECT (Iska hona zaroori hai) ---
const styles = {
    fullPage: {
        height: '100vh',
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
        backgroundColor: '#020617',
        fontFamily: "'Segoe UI', Tahoma, Geneva, Verdana, sans-serif"
    },
    loginCard: {
        backgroundColor: '#1e293b',
        padding: '40px',
        borderRadius: '20px',
        textAlign: 'center',
        width: '380px',
        border: '1px solid #38bdf8',
        boxShadow: '0 0 30px rgba(56, 189, 248, 0.2)'
    },
    iconCircle: {
        fontSize: '40px',
        marginBottom: '15px'
    },
    input: {
        width: '100%',
        padding: '12px',
        marginBottom: '15px',
        borderRadius: '8px',
        border: '1px solid #334155',
        backgroundColor: '#0f172a',
        color: 'white',
        outline: 'none',
        boxSizing: 'border-box'
    },
    userBtn: {
        width: '100%',
        padding: '12px',
        backgroundColor: '#3b82f6',
        color: 'white',
        border: 'none',
        borderRadius: '8px',
        cursor: 'pointer',
        fontWeight: 'bold',
        fontSize: '16px',
        marginTop: '10px',
        transition: '0.3s'
    },
    backLink: {
        color: '#64748b',
        marginTop: '25px',
        cursor: 'pointer',
        fontSize: '14px',
        display: 'block',
        textDecoration: 'underline'
    }
};

export default UserLogin;
