import React from "react";
import { BrowserRouter as Router, Routes, Route, useLocation } from "react-router-dom";
import Sidebar from "./components/Sidebar";
import Navbar from "./components/Navbar";
import Dashboard from "./pages/Dashboard";
import History from "./pages/History";
import Settings from "./pages/Settings";
import LandingPage from "./pages/LandingPage"; // Naya page import kiya

// Yeh ek helper component hai jo check karega ke Sidebar/Navbar dikhana hai ya nahi
const Layout = ({ children }) => {
  const location = useLocation();
  // Agar hum landing page ("/") par hain, to Sidebar/Navbar mat dikhao
  const isLandingPage = location.pathname === "/";

  if (isLandingPage) {
    return <div>{children}</div>;
  }

  // Baqi sab pages par Sidebar aur Navbar dikhao
  return (
    <div style={{ display: "flex", backgroundColor: "#0f172a", minHeight: "100vh", color: "white" }}>
      <Sidebar />
      <div style={{ flex: 1, display: "flex", flexDirection: "column" }}>
        <Navbar />
        <main style={{ flex: 1, padding: "20px", overflowY: 'auto' }}>
          {children}
        </main>
      </div>
    </div>
  );
};

function App() {
  return (
    <Router>
      <Layout>
        <Routes>
          {/* Sabse pehle Landing Page khulega */}
          <Route path="/" element={<LandingPage />} />
          
          {/* Login ke baad yeh pages ayenge */}
          <Route path="/dashboard" element={<Dashboard />} />
          <Route path="/history" element={<History />} />
          <Route path="/settings" element={<Settings />} />
        </Routes>
      </Layout>
    </Router>
  );
}

export default App;
