import React from 'react';

const Sidebar = () => {
    const menuItems = ["Dashboard", "Analysis History", "Threat Reports", "Settings"];
    
    return (
        <div style={{ width: '240px', backgroundColor: '#0f172a', height: '100vh', padding: '20px', borderRight: '1px solid #334155' }}>
            <div style={{ marginBottom: '40px', textAlign: 'center' }}>
                <div style={{ width: '50px', height: '50px', backgroundColor: '#3b82f6', borderRadius: '50%', margin: '0 auto 10px' }}></div>
                <small style={{ color: '#94a3b8' }}>Admin Panel</small>
            </div>
            {menuItems.map(item => (
                <div key={item} style={{ 
                    padding: '12px 15px', color: '#cbd5e1', cursor: 'pointer', 
                    borderRadius: '8px', marginBottom: '5px', transition: '0.3s',
                    backgroundColor: item === "Dashboard" ? '#1e293b' : 'transparent'
                }}>
                    {item}
                </div>
            ))}
        </div>
    );
};

export default Sidebar;
