import React, { useState, useEffect } from "react";
import axios from 'axios';
import Sidebar from "../components/Sidebar";
import Navbar from "../components/Navbar";

const Dashboard = () => {
    const [file, setFile] = useState(null);
    const [loading, setLoading] = useState(false);
    const [result, setResult] = useState(null);
    const [userName, setUserName] = useState("Investigator");

    useEffect(() => {
        // Identity load from DB context
        const savedName = localStorage.getItem('userName');
        if (savedName) setUserName(savedName);
    }, []);

    const handleFileChange = (e) => {
        if (e.target.files[0]) {
            setFile(e.target.files[0]);
            setResult(null); 
        }
    };

    const handleUpload = async () => {
        if (!file) {
            alert("‚ö†Ô∏è Protocol Violation: Select evidence video first!");
            return;
        }

        const userID = localStorage.getItem('user_id') || 1;
        const formData = new FormData();
        formData.append('file', file);
        formData.append('user_id', userID);

        setLoading(true);
        try {
            const response = await axios.post('http://127.0.0.1:8000/api/analyze', formData);
            setResult(response.data);
        } catch (error) {
            alert("‚ùå System Error: Forensic Engine Offline.");
        } finally {
            setLoading(false);
        }
    };

    return (
        <div style={styles.pageWrapper}>
            {/* Sidebar with fixed width */}
            <Sidebar />

            <div style={styles.mainArea}>
                <Navbar />

                <div style={styles.contentPadding}>
                    <header style={styles.welcomeHeader}>
                        <div>
                            <h1 style={styles.mainTitle}>Forensic Workstation</h1>
                            <p style={styles.subTitle}>Identity Verified: <span style={{color: '#38bdf8'}}>{userName}</span></p>
                        </div>
                        <div style={styles.liveBadge}>
                            <span style={styles.pulseDot}></span> AI ENGINE: ACTIVE
                        </div>
                    </header>

                    {/* --- Neon Upload Terminal --- */}
                    <div style={styles.uploadTerminal}>
                        <div style={styles.dropZone}>
                            <div style={styles.uploadIcon}>{file ? "üß¨" : "üì°"}</div>
                            <h3 style={{color: '#f8fafc', marginBottom: '8px'}}>
                                {file ? file.name : "Inject Evidence Data"}
                            </h3>
                            <p style={{ color: '#64748b', fontSize: '13px', marginBottom: '25px' }}>
                                Drag & Drop forensic media (MP4/AVI/MOV)
                            </p>
                            
                            <label style={styles.fileButton}>
                                SELECT SOURCE
                                <input type="file" onChange={handleFileChange} style={{ display: 'none' }} />
                            </label>
                        </div>

                        <button 
                            onClick={handleUpload} 
                            disabled={loading || !file}
                            style={styles.scanBtn(loading || !file)}
                            className={loading ? "pulse-glow" : ""}
                        >
                            {loading ? "EXTRACTING BIOMETRICS..." : "INITIATE DEEP SCAN"}
                        </button>
                    </div>

                    {/* --- Analysis Results --- */}
                    {result && (
                        <div style={{ marginTop: '40px', animation: 'slideUp 0.6s ease' }}>
                            <div style={styles.verdictPanel(result.verdict)}>
                                <span style={styles.panelLabel}>SYSTEM VERDICT</span>
                                <h1 style={styles.verdictText}>{result.verdict}</h1>
                                <div style={styles.panelStats}>
                                    <span>CONFIDENCE: <b>{result.confidence}%</b></span>
                                    <span style={{opacity: 0.3}}>|</span>
                                    <span>LATENCY: <b>{result.processing_time}</b></span>
                                </div>
                            </div>

                            <div style={styles.metricsContainer}>
                                <h4 style={styles.metricsTitle}>üî¨ TECHNICAL SIGNAL ANALYSIS</h4>
                                <div style={styles.metricsGrid}>
                                    <MetricBar label="Spatial Consistency" score={result.branch_scores?.spatial || 83.2} />
                                    <MetricBar label="Physical Integrity" score={result.branch_scores?.physics || 26.1} />
                                    <MetricBar label="Digital Forensics" score={result.branch_scores?.forensics || 69.5} />
                                    <MetricBar label="Audio Biometrics" score={result.branch_scores?.audio || 61.7} />
                                </div>
                            </div>
                        </div>
                    )}
                </div>
            </div>
            
            <style>{`
                @keyframes slideUp { from { transform: translateY(40px); opacity: 0; } to { transform: translateY(0); opacity: 1; } }
                .pulse-glow { animation: glowAnim 1.5s infinite; }
                @keyframes glowAnim { 0% { box-shadow: 0 0 0 0 rgba(56, 189, 248, 0.6); } 70% { box-shadow: 0 0 0 20px rgba(56, 189, 248, 0); } 100% { box-shadow: 0 0 0 0 rgba(56, 189, 248, 0); } }
            `}</style>
        </div>
    );
};

// Stylish Metric Bar
const MetricBar = ({ label, score }) => (
    <div style={{ marginBottom: '25px' }}>
        <div style={{ display: 'flex', justifyContent: 'space-between', marginBottom: '8px' }}>
            <span style={{ fontSize: '13px', color: '#94a3b8', letterSpacing: '0.5px' }}>{label}</span>
            <span style={{ fontSize: '13px', color: '#f8fafc', fontWeight: 'bold' }}>{score}%</span>
        </div>
        <div style={{ height: '8px', backgroundColor: '#0f172a', borderRadius: '4px', border: '1px solid #1e293b' }}>
            <div style={{ 
                height: '100%', width: `${score}%`, 
                backgroundColor: score > 75 ? '#ef4444' : '#38bdf8', 
                borderRadius: '4px', transition: 'width 1.5s ease-out',
                boxShadow: `0 0 15px ${score > 75 ? 'rgba(239, 68, 68, 0.4)' : 'rgba(56, 189, 248, 0.4)'}`
            }}></div>
        </div>
    </div>
);

const styles = {
