import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';

const UserLogin = () => {
    const navigate = useNavigate();
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [loading, setLoading] = useState(false);

    const handleLogin = async (e) => {
        e.preventDefault();
        setLoading(true);
        try {
            const res = await axios.post('http://127.0.0.1:8000/api/login', { email, password });
            
            // --- üõ°Ô∏è GOVERNANCE SESSION MANAGEMENT ---
            // In keys ka naam Sidebar aur Dashboard ke logic se match hona zaroori hai
            localStorage.setItem('role', res.data.role);       // 'admin' ya 'operator'
            localStorage.setItem('userName', res.data.full_name);
            localStorage.setItem('user_id', res.data.user_id);

            // Access Granted!
            navigate('/dashboard');
        } catch (err) {
            const errorMsg = err.response?.data?.detail || "Access Denied: Invalid Credentials";
            alert(errorMsg);
        } finally {
            setLoading(false);
        }
    };

    return (
        <div style={styles.container}>
            <div style={styles.card}>
                <div style={{fontSize: '50px', marginBottom: '10px'}}>üë§</div>
                <h2 style={{color: '#38bdf8', marginBottom: '10px'}}>Identity Access</h2>
                <p style={{color: '#94a3b8', fontSize: '14px', marginBottom: '30px'}}>
                    Secure Login to DeepGuard Network
                </p>

                <form onSubmit={handleLogin}>
                    <input 
                        style={styles.input} 
                        type="email" 
                        placeholder="Authorized Email" 
                        value={email}
                        onChange={e => setEmail(e.target.value)} 
                        required 
                    />
                    <input 
                        style={styles.input} 
                        type="password" 
                        placeholder="Secure Password" 
                        value={password}
                        onChange={e => setPassword(e.target.value)} 
                        required 
                    />
                    <button type="submit" style={styles.btn} disabled={loading}>
                        {loading ? "Authenticating..." : "Establish Connection"}
                    </button>
                </form>

                <p style={{marginTop: '20px', color: '#64748b', fontSize: '14px'}}>
                    New investigator? <span onClick={() => navigate('/register')} style={styles.link}>Register Here</span>
                </p>
            </div>
        </div>
    );
};

const styles = {
    container: { height: '100vh', display: 'flex', justifyContent: 'center', alignItems: 'center', backgroundColor: '#020617', fontFamily: 'Inter, sans-serif' },
    card: { backgroundColor: '#1e293b', padding: '40px', borderRadius: '24px', textAlign: 'center', width: '400px', border: '1px solid rgba(56, 189, 248, 0.2)', boxShadow: '0 20px 50px rgba(0,0,0,0.5)' },
    input: { width: '100%', padding: '14px', marginBottom: '15px', borderRadius: '10px', border: '1px solid #334155', backgroundColor: '#0f172a', color: 'white', boxSizing: 'border-box', outline: 'none', transition: '0.3s' },
    btn: { width: '100%', padding: '14px', backgroundColor: '#3b82f6', color: 'white', border: 'none', borderRadius: '10px', cursor: 'pointer', fontWeight: 'bold', fontSize: '16px', boxShadow: '0 4px 12px rgba(59, 130, 246, 0.3)' },
    link: { color: '#38bdf8', cursor: 'pointer', fontWeight: 'bold', textDecoration: 'underline' }
};

export default UserLogin;
